package org.ujorm2.metamodel;

import javax.annotation.Nonnull;
import org.ujorm2.core.UjoContext;

/**
 * TODO: A helper class generated by a Maven module.
 * @author Pavel Ponec
 */
public class ModelProvider {

    private final MetaItem item;
    private final MetaOrder order;
    private final MetaUser user;
    private final MetaAnonymous anonumous;

    public ModelProvider() {
        this(new UjoContext());
    }

    public ModelProvider(@Nonnull final UjoContext context) {
        item = new MetaItem(context);
        order = new MetaOrder(context);
        user = new MetaUser(context);
        anonumous = new MetaAnonymous(context);
    }

    public MetaItem item() {
        return item;
    }

    public MetaOrder order() {
        return order;
    }

    public MetaUser user() {
        return user;
    }

    public MetaAnonymous anonumous() {
        return anonumous;
    }

}
