package org.ujorm2.metamodel;

import javax.annotation.Nonnull;
import org.ujorm2.core.MetaStore;
import org.ujorm2.core.UjoContext;
import org.ujorm2.doman.Anonymous;
import org.ujorm2.doman.Item;
import org.ujorm2.doman.Order;
import org.ujorm2.doman.User;

/**
 * TODO: A helper class generated by a Maven module.
 * @author Pavel Ponec
 */
public class ModelProvider implements UjoContext {

    private final MetaStore store = new MetaStore();

    private final MetaItem item = store.addModel(new MetaItem());
    private final MetaOrder order = store.addModel(new MetaOrder());
    private final MetaUser user = store.addModel(new MetaUser());
    private final MetaAnonymous anonumous = store.addModel(new MetaAnonymous());

    public ModelProvider() {
        store.close();
    }

    /** provide  */
    @Nonnull
    public MetaStore getModels$() {
        return store;
    }

    public MetaItem<Item> item() {
        return item;
    }

    public MetaOrder<Order> order() {
        return order;
    }

    public MetaUser<User> user() {
        return user;
    }

    public MetaAnonymous<Anonymous> anonumous() {
        return anonumous;
    }

}
